mapper.select:
        SELECT


--         级联表的字段
        ,laoshi.laoshi_name as laoshiName
        ,laoshi.laoshi_phone as laoshiPhone
        ,laoshi.laoshi_photo as laoshiPhoto
        ,laoshi.laoshi_email as laoshiEmail

        FROM huodong  a
        left JOIN laoshi laoshi ON a.laoshi_id = laoshi.id



                and a.id in

                #{item}



                and (
                    a.laoshi_id = #{params.laoshiId}
                )


                and a.huodong_name like CONCAT('%',#{params.huodongName},'%')


                and a.huodong_types = #{params.huodongTypes}


                  and a.huodong_clicknum >= #{params.huodongClicknumStart}   


                  and a.huodong_clicknum <= #{params.huodongClicknumEnd}   


                and a.huodong_clicknum = #{params.huodongClicknum}


                  and a.huodong_delete >= #{params.huodongDeleteStart}   


                  and a.huodong_delete <= #{params.huodongDeleteEnd}   


                and a.huodong_delete = #{params.huodongDelete}


                and a.huodong_content like CONCAT('%',#{params.huodongContent},'%')


                and a.shangxia_types = #{params.shangxiaTypes}




                and a.laoshi_id IS NOT NULL


                and laoshi.laoshi_name like CONCAT('%',#{params.laoshiName},'%')


                and laoshi.laoshi_phone like CONCAT('%',#{params.laoshiPhone},'%')


                and laoshi.laoshi_email like CONCAT('%',#{params.laoshiEmail},'%')



        order by a.${params.orderBy} desc
